import React, { useState } from 'react';

function Resources({ user, setPage, setUser }) {
  const [message, setMessage] = useState('');
  const [attendance, setAttendance] = useState([]);

  const handleLogout = async () => {
    setMessage('');
    try {
      const res = await fetch('http://localhost:5000/api/users/logout', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId: user._id })
      });
      if (res.ok) {
        setUser(null);
        setPage('login');
      } else {
        setMessage('Logout failed');
      }
    } catch {
      setMessage('Server error');
    }
  };

  const handleAttendance = async () => {
    setMessage('');
    try {
      const res = await fetch('http://localhost:5000/api/users/mark-attendance', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId: user._id })
      });
      const data = await res.json();
      if (res.ok) {
        setAttendance(data.attendance);
        setMessage('Attendance marked!');
      } else {
        setMessage(data.error || 'Attendance failed');
      }
    } catch {
      setMessage('Server error');
    }
  };

  return (
    <div>
      <h2>Welcome, {user.name}</h2>
      <button onClick={handleLogout}>Logout</button>
      <button onClick={handleAttendance}>Mark Attendance</button>
      {/* Add update/delete user buttons as needed */}
      {message && <div>{message}</div>}
      <div>
        <h4>Attendance:</h4>
        <ul>
          {attendance.map((a, i) => <li key={i}>{a.date}</li>)}
        </ul>
      </div>
    </div>
  );
}

export default Resources;
